package Arrayy;

public class strarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String[] s=new String[3];
		s[0]="sangeeth";
		s[1]="sathya";
		int a=s.length;
		
	for (int i=0;i<a;i++)
		{
			System.out.println(s[i]);
			
		}
		

	}

}
